mapper.select:
SELECT LIST.* , Q.NAME , Q.TYPE , Q.CONTENT , Q.OBJECTIVES , Q.SELECT_TYPE , Q.SELECT_VAL , Q.SOLUTION FROM TBL_QUESTION_LIST LIST JOIN TBL_QUESTION Q ON LIST.QUESTION_IDX = Q.IDX
WHERE EXAM_IDX = #{exam_idx}
ORDER BY SEQ

mapper.update:
UPDATE TBL_QUESTION_LIST SET
QUESTION_IDX = #{question_idx},
UPDATE_TM = now() WHERE IDX = #{idx}

SET @seq = 0;
UPDATE TBL_QUESTION_LIST
SET seq = @seq := @seq + 1
WHERE exam_idx = #{exam_idx}
ORDER BY seq

mapper.select:
SELECT name , inquiries FROM TBL_EXAM_RESULT WHERE 1=1 AND EXAM_IDX = #{idx}
AND ${SEARCH_TYPE} LIKE '%${SEARCH_TEXT}%'
order by ${UP} asc
order by ${DOWN} desc
order by CREATE_TM desc
LIMIT ${LIMIT} OFFSET ${OFFSET}

SELECT idx , select_list , sex , school_year , address_local FROM TBL_EXAM_RESULT
WHERE 1=1
AND EXAM_IDX = #{idx}
